import React, { useState } from 'react';
import "./style/Login.css";

const handleLogin = async (e) => {
  console.log("prueba");
  e.preventDefault();

    const dataprueba = fetch("https://dev.tuten.cl/TutenREST/rest/user/testapis%40tuten.cl", {
        method: "PUT",
        headers: {
        "Accept": "application/json",
        "app": "APP_BCK",
        "email": "testapis@tuten.cl",
        "password": "1234"
        }
    }).then((data) => data.json());

    console.log(dataprueba)

};



class Login extends React.Component {

    constructor(props){
        super(props);
        this.state = {email: '',password:''};

        this.handleChange = this.handleChange.bind(this);
    }

  handleChange(event) {
      console.log(event.target.value)
      this.setState({email: event.target.value});
  }

  render() {
    return (
      <div className="login-wrapper">
        <div className="g-form-container">
          <div className="g-form">
            <form className="g-form" onSubmit={handleLogin}>
              <h1>BIENVENIDO</h1>
              <label className="form-label">
                <p>USUARIO (testapis@tuten.cl)</p>
                <input type="text" placeholder="Ingrese correo" value={this.state.email}  onChange={this.handleChange} />
              </label>
              <label className="form-label">
                <p>CONTRASEÑA (1234)</p>
                <input type="password" placeholder="Ingrese contraseña" />
              </label>
              <div>
                <button type="submit">INGRESAR</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
