import React, { useState } from 'react';
import "../style/Login.css";




class Login extends React.Component {

    constructor(props){
        super(props);
        this.state = {};
    }

    handleLogin = async (e) => {

      e.preventDefault();
      const email = document.getElementsByClassName('email')[0].value;
      const pass = document.getElementsByClassName('pass')[0].value;
      console.log(email,pass);

        const dataprueba = fetch("https://dev.tuten.cl/TutenREST/rest/user/testapis%40tuten.cl", {
            method: "PUT",
            headers: {
            "Accept": "application/json",
            "app": "APP_BCK",
            "email": email,
            "password": pass
            }
        }).then((data) => data.json());

        this.props.history.push('/lista');
        console.log(dataprueba)

    };

  render() {
    return (
      <div className="login-wrapper">
        <div className="g-form-container">
          <div className="g-form">
            <form className="g-form" onSubmit={this.handleLogin}>
              <h1>BIENVENIDO</h1>
              <label className="form-label">
                <p> <strong>USUARIO</strong> (testapis@tuten.cl)</p>
                <input type="text" placeholder="Ingrese correo" className="email"/>
              </label>
              <label className="form-label">
                <p><strong>CONTRASEÑA</strong> (1234)</p>
                <input type="password" placeholder="Ingrese contraseña"  className="pass"  />
              </label>
              <div>
                <button type="submit"  onSubmit={this.handleLogin} className="btn btn-primary" >INGRESAR</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
